import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import ItemList from './components/ItemList';
import AddItemForm from './components/AddItemForm';

function App() {
  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:8000/api/todos/')
      .then(response => setItems(response.data.map(item => item.title)))
      .catch(error => {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      });
  }, []);

  const addItem = (item) => {
    const itemLowerCase = item.toLowerCase();
    const itemsLowerCase = items.map((i) => i.toLowerCase());

    if (!itemsLowerCase.includes(itemLowerCase)) {
      axios.post('http://localhost:8000/api/todos/', { title: item })
        .then(response => setItems([...items, response.data.title]))
        .catch(error => {
          console.error('Error adding item:', error);
          setError('Error adding item');
        });
    } else {
      alert("Item already exists!");
    }
  };

  const deleteItem = (index) => {
    const itemToDelete = items[index];
    axios.get('http://localhost:8000/api/todos/')
      .then(response => {
        const todo = response.data.find(todo => todo.title === itemToDelete);
        if (todo) {
          axios.delete(`http://localhost:8000/api/todos/${todo.id}/`)
            .then(() => setItems(items.filter((_, i) => i !== index)))
            .catch(error => {
              console.error('Error deleting item:', error);
              setError('Error deleting item');
            });
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setError('Error fetching data');
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>My ToDo List</h1>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <ItemList items={items} deleteItem={deleteItem} />
        <AddItemForm addItem={addItem} />
      </header>
    </div>
  );
}

export default App;
